mapper.select:
SELECT ARCHIVES_ID archivesId, ARCHIVES_CODE archivesCode, DEPT_CODE deptCode, to_char(request_date,'yyyy-mm-dd') requestDate, SCHOOL_CODE schoolCode, FUNC_GET_TABLEVALUE(ONE_LEVEL,'DZDA_TYPE','TYPE_ID','TYPE_NAME') oneLevel, FUNC_GET_TABLEVALUE(TWO_LEVEL,'DZDA_TYPE','TYPE_ID','TYPE_NAME') twoLevel, FUNC_GET_DICVALUE(FILE_TYPE,'DALX') fileType, CASE WHEN REQUEST_FLAG='0' THEN '已提交' WHEN REQUEST_FLAG='1' THEN '待审核' WHEN REQUEST_FLAG='2' THEN '通过' WHEN REQUEST_FLAG='3' THEN '驳回' ELSE '无' END state, REQUEST_FLAG requestFlag, REQUEST_TYPE requestType, REMARK remark, EDITED_ID editedId, FUNC_GET_USERNAME(CREATOR) creator, FUNC_GET_DEPTNAME(CREATE_DEPT) createDept, CREATE_TIME createTime FROM DZDA_MESSAGE WHERE 1=1 AND VALID_FLAG=#{validFlag} AND DEL_STATE = '0'
AND CREATOR= #{creator}
AND CREATE_DEPT= #{createDept}
AND FUNC_GET_DEPTNAME(CREATE_DEPT) LIKE '%'||#{deptName}||'%'
AND FUNC_GET_USERNAME(CREATOR) LIKE '%'||#{personName}||'%'
AND SCHOOL_CODE=#{schoolCode}
AND DEPT_CODE=#{deptCode}
AND request_date= #{requestDate}
AND ONE_LEVEL=#{oneLevel}
AND TWO_LEVEL=#{twoLevel}
AND FILE_TYPE=#{fileType}
AND REQUEST_FLAG=#{requestFlag}
ORDER BY CREATE_TIME DESC

mapper.update:
UPDATE DZDA_MESSAGE SET
ARCHIVES_CODE=(select decode(ARCHIVES_CODE,'',#{archivesCode}||'0001',ARCHIVES_CODE) from (select lpad(max(to_number(ARCHIVES_CODE))+1,19,'0') as ARCHIVES_CODE from DZDA_MESSAGE where ARCHIVES_CODE like #{archivesCode}||'%')),
request_date=to_date(#{requestDate},'yyyy-mm-dd'), ONE_LEVEL=#{oneLevel}, TWO_LEVEL=#{twoLevel}, FILE_TYPE=#{fileType}, REMARK=#{remark}, REQUEST_FLAG='', CHANGER=#{changer}, CHANGE_DEPT=#{changeDept}, CHANGE_TIME=sysdate WHERE ARCHIVES_ID=#{archivesId}

UPDATE DZDA_MESSAGE SET
VALID_FLAG=#{validFlag}
WHERE ARCHIVES_ID=#{archivesId}

mapper.select:
SELECT log_id, ARCHIVES_ID, PERSON_ID, DEPT_ID, OPERATE_TYPE, FUNC_GET_DICVALUE(OPERATE_TYPE,'ZYK_CZLX') operateType, FUNC_GET_USERNAME(PERSON_ID) personId, FUNC_GET_DEPTNAME(DEPT_ID) deptId, to_char(OPERATE_TIME, 'yyyy-mm-dd hh24:mi') operateTime, remark from DZDA_LOG where VALID_FLAG='1'
and OPERATE_TYPE=#{operateType}
order by operateTime desc

mapper.update:
UPDATE DZDA_MESSAGE SET
REQUEST_FLAG=#{requestFlag}
WHERE ARCHIVES_ID=#{archivesId}

UPDATE DZDA_MESSAGE SET
REASON =#{reason},
REQUEST_FLAG=#{requestFlag}
WHERE ARCHIVES_ID=#{archivesId}

mapper.select:
SELECT ARCHIVES_ID archivesId, ARCHIVES_CODE archivesCode, DEPT_CODE deptCode, to_char(request_date,'yyyy-mm-dd') requestDate, SCHOOL_CODE schoolCode, FUNC_GET_TABLEVALUE(ONE_LEVEL,'DZDA_TYPE','TYPE_ID','TYPE_NAME') oneLevel, FUNC_GET_TABLEVALUE(TWO_LEVEL,'DZDA_TYPE','TYPE_ID','TYPE_NAME') twoLevel, FUNC_GET_DICVALUE(FILE_TYPE,'DALX') fileType, CASE WHEN REQUEST_FLAG='0' THEN '已提交' WHEN REQUEST_FLAG='1' THEN '待审核' WHEN REQUEST_FLAG='2' THEN '通过' WHEN REQUEST_FLAG='3' THEN '驳回' ELSE '无' END state, REQUEST_FLAG requestFlag, REQUEST_TYPE requestType, REMARK remark, EDITED_ID editedId, FUNC_GET_USERNAME(CREATOR) creator, FUNC_GET_DEPTNAME(CREATE_DEPT) createDept, CREATE_TIME createTime FROM DZDA_MESSAGE WHERE 1=1 AND DEL_STATE = '1'
AND CHANGER= #{changer}
AND FUNC_GET_DEPTNAME(CREATE_DEPT) LIKE '%'||#{deptName}||'%'
AND FUNC_GET_USERNAME(CREATOR) LIKE '%'||#{personName}||'%'
AND SCHOOL_CODE=#{schoolCode}
AND DEPT_CODE=#{deptCode}
AND request_date= #{requestDate}
AND ONE_LEVEL=#{oneLevel}
AND TWO_LEVEL=#{twoLevel}
AND FILE_TYPE=#{fileType}
AND REQUEST_FLAG=#{requestFlag}
ORDER BY CREATE_TIME DESC
