mapper.select:
SELECT DISTINCT t1.COURSE_ID, t1.COURSE_NAME, FUNC_GET_DICVALUE(t1.COURSE_TYPE, 'KCLX') COURSE_TYPE, t1.create_time createTime, t1.course_code courseCode, FUNC_GET_DICVALUE(t1.COURSE_PROPERTIES,'KCJSSX') courseProperties, FUNC_GET_DICVALUE(t1.SCHOOL_ENTERPRISE_COOPERATION,'KCSF') schoolEnterpriseCooperation, FUNC_GET_DICVALUE(t1.CORE_CURRICULUM,'KCSF') coreCurriculum, FUNC_GET_DICVALUE(t1.EXCELLENT_COURSE,'KCJPKC') excellentCourse, FUNC_GET_DICVALUE(t1.VENUE,'KCSKDD') venue, FUNC_GET_DICVALUE(t1.TEST_METHOD,'KCKSKHFF') testMethod, FUNC_GET_DICVALUE(t1.CLASS_CERTIFIATE,'KCSF') classCertifiate, t1.PRESCRIBED_PERIODS prescribedPeriods, t1.PRACTICE_CLASS, FUNC_GET_DICVALUE(t1.TEACHING_METHOD,'ZYSKFS') teachingMethod FROM T_JW_COURSE t1 WHERE 1=1
AND t1.COURSE_NAME = #{courseName}
AND t1.DEPARTMENTS_ID = #{departmentsId}
AND t1.MAJOR_CODE = #{majorCode}

select * from T_JW_COURSE
where COURSE_TYPE = #{courseType} AND COURSE_NAME = #{courseName}
AND CREATE_DEPT LIKE func_get_range('${createDept}','6')

    AND COURSE_ID != #{courseId}


    AND DEPARTMENTS_ID = #{departmentsId}
    AND MAJOR_CODE = #{majorCode}
    AND TRAINING_LEVEL = #{trainingLevel}

select max(to_number(COURSE_CODE)) from T_JW_COURSE
where  DEPARTMENTS_ID = #{departmentsId}
 AND MAJOR_CODE = #{majorCode}
  AND TRAINING_LEVEL = #{trainingLevel}
