mapper.select:
SELECT t.id, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_USERNAME(t.requester) requester, t.file_title fileTitle, t.delivery_emp_id deliveryEmpId, t.make_emp_id makeEmpId, FUNC_GET_USERNAME(t.delivery_emp_id) deliveryEmpIdShow, FUNC_GET_USERNAME(t.make_emp_id) makeEmpIdShow, FUNC_GET_DICVALUE(t.secret_class,'MJ') secretClass, t.title title, t.printing_number printingNumber, t.symbol symbol, t.request_flag, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_BG_DOCUMENT_WF t where 1 = 1 AND t.request_flag = '0'
AND to_char(t.request_date,'yyyy-mm-dd') LIKE #{requestDate}
AND t.create_dept= #{createDept}
AND t.symbol LIKE '%'||#{symbol}||'%'
ORDER BY t.create_time desc

SELECT t.id, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_USERNAME(t.requester) requester, t.file_title fileTitle, t.delivery_emp_id deliveryEmpId, t.make_emp_id makeEmpId, FUNC_GET_USERNAME(t.delivery_emp_id) deliveryEmpIdShow, FUNC_GET_USERNAME(t.make_emp_id) makeEmpIdShow, FUNC_GET_DICVALUE(t.secret_class,'MJ') secretClass, t.title title, t.printing_number printingNumber, t.symbol symbol, t.request_flag, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_BG_DOCUMENT_WF t,t_sys_workflow_start s,t_sys_workflow_handle r where 1 = 1
AND r.handle_user= #{creator}
AND func_get_deptname(t.request_dept) LIKE #{requestDept}
AND r.handle_dept= #{createDept}
AND FUNC_GET_USERNAME(t.requester) LIKE #{requester}
AND to_char(t.request_date,'yyyy-mm-dd') LIKE #{requestDate}
AND (r.state='1' or r.state='3')AND t.id = s.business_id AND s.start_id = r.start_id AND t.request_flag != '4' ORDER BY t.create_time desc

SELECT DISTINCT t.id, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_USERNAME(t.requester) requester, t.file_title fileTitle, t.delivery_emp_id deliveryEmpId, t.make_emp_id makeEmpId, FUNC_GET_DICVALUE(t.secret_class,'MJ') secretClass, t.title title, FUNC_GET_USERNAME(t.delivery_emp_id) deliveryEmpIdShow, FUNC_GET_USERNAME(t.make_emp_id) makeEmpIdShow, t.printing_number printingNumber, t.symbol symbol, FUNC_GET_DICVALUE(t.request_flag,'GZLLZZT') requestFlag, FUNC_GET_DICVALUE(t.feedback_flag,'GZLFKZT') feedbackFlag, t.feedback feedBack, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_BG_DOCUMENT_WF t,t_sys_workflow_start s,t_sys_workflow_handle r where 1 = 1
AND r.handle_user= #{creator}
AND func_get_deptname(t.request_dept) LIKE #{requestDept}
AND FUNC_GET_USERNAME(t.requester) LIKE #{requester}
AND r.handle_dept= #{createDept}
AND to_char(t.request_date,'yyyy-mm-dd') LIKE #{requestDate}
AND r.state='2' AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time desc

select remark from (select t.handle_name||':'||t.remark remark
                    from T_SYS_WORKFLOW_HANDLE t
                    where t.cuurent_workflow_id = #{workflowId}
                          and FUNC_GET_TABLEVALUE(t.handle_role, 'T_SYS_ROLE', 'ROLE_ID', 'ROLE_NAME') = '院长'
                          and t.start_id = #{startId}
                    order by t.create_time desc)  where rownum = 1
