mapper.select:
SELECT
t.dorm_name dormName, FUNC_GET_TABLEVALUE(t.building_id,'T_JW_BUILDING','ID','BUILDING_NAME') buildingId, t.people_number peopleNumber, lc.dic_name floor, qslx.dic_name dormTypeShow, FUNC_GET_DICVALUE(t.use_status,'SYZT') useStatus, t.remark remark, t.creator creator, t.create_time createTime, t.create_dept createDept, t.changer changer, t.change_time changeTime, t.change_dept changeDept, t.id id, t.valid_flag validFlag FROM T_JW_DORM t left join (SELECT t.dic_name, t.dic_code FROM t_sys_dic t WHERE t.parent_id = (SELECT s.id FROM t_sys_dic s where s.dic_code = 'QSLX' )) qslx on qslx.dic_code = t.dorm_type left join (SELECT t.dic_name, t.dic_code FROM t_sys_dic t WHERE t.parent_id = (SELECT s.id FROM t_sys_dic s where s.dic_code = 'LC' )) lc on lc.dic_code = t.floor WHERE 1 = 1
AND t.dorm_name LIKE #{dormName}
AND t.floor LIKE #{floor}
AND t.use_status LIKE #{useStatus}
ORDER BY t.create_time DESC
