mapper.select:
SELECT employment_unit_id employmentUnitId, internship_unit_name internshipUnitName, area area, address address, contact_person contactPerson, contact_number contactNumber, FUNC_GET_DICVALUE(internship_unit_flag,'SF') internshipUnitFlag, unit_encoding unitEncoding, legal_person legalPerson, registered_capital registeredCapital, person_number personNumber, enterprise_scale enterpriseScale, FUNC_GET_DICVALUE(enterprise_scale,'QYGM') enterpriseScaleShow, FUNC_GET_DICVALUE(unit_property,'JYDWXZ') unitPropertyShow, FUNC_GET_DICVALUE(employment_industry,'JYCYLB') employmentIndustryShow, FUNC_GET_DICVALUE(employment_area,'JYDYLB') employmentAreaShow, FUNC_GET_DICVALUE(employment_place,'JYDD') employmentPlaceShow, FUNC_GET_DICVALUE(enterprise_category,'JYQYSSLB') enterpriseCategoryShow, FUNC_GET_DICVALUE(employment_channels,'JYQD') employmentChannelsShow, FUNC_GET_DICVALUE(counterpart_property,'ZYDKXZ') counterpartPropertyShow, FUNC_GET_DICVALUE(employment_nature,'JYXZ') employmentNatureShow, create_time createTime FROM T_XG_EMPLOYMENT_UNIT WHERE 1 = 1 AND valid_flag = 1
AND internship_unit_name LIKE #{internshipUnitName}
order by create_time desc
