mapper.select:
select t.id id, FUNC_GET_USERNAME(t.manager) manager, func_get_deptname(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, t.remark remark, t.reason reason, t.lowercase_amount amount, t.request_flag requestFlag, t."UPPERCASE AMOUNT" amountUpper, t.creator creator, t.create_time createTime from T_BG_FUNDS2_WF t WHERE 1 = 1
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND t.creator= #{creator}
AND t.create_dept= #{createDept}
AND t.request_flag = '0' ORDER BY t.create_time DESC

select t.id id, FUNC_GET_USERNAME(t.manager) manager, func_get_deptname(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd') requestDate, t.remark remark, t.reason reason, t.lowercase_amount amount, t.request_flag requestFlag, t."UPPERCASE AMOUNT" amountUpper, t.creator creator, t.create_time createTime FROM T_BG_Funds2_WF t,T_SYS_WORKFLOW_START s,T_SYS_WORKFLOW_HANDLE r WHERE 1 = 1
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND FUNC_GET_USERNAME(t.manager) LIKE #{manager}
AND func_get_deptname(t.request_dept) LIKE #{requestDept}
AND r.handle_user= #{creator}
AND r.handle_dept= #{createDept}
AND (r.state='1' or r.state = '3') AND t.id = s.business_id AND s.start_id = r.start_id AND t.request_flag != '4' ORDER BY t.create_time DESC

select DISTINCT t.id id, FUNC_GET_USERNAME(t.manager) manager, func_get_deptname(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd') requestDate, t.remark remark, t.reason reason, t.lowercase_amount amount, t."UPPERCASE AMOUNT" amountUpper, t.creator creator, t.create_time createTime, t.feedback feedback, FUNC_GET_DICVALUE(t.request_flag,'GZLLZZT') requestFlag, FUNC_GET_DICVALUE(t.feedback_flag,'GZLFKZT') feedbackFlag from T_BG_Funds2_WF t,T_SYS_WORKFLOW_START s,T_SYS_WORKFLOW_HANDLE r WHERE 1 = 1
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND FUNC_GET_USERNAME(t.manager) LIKE #{manager}
AND func_get_deptname(t.request_dept) LIKE #{requestDept}
AND r.handle_user= #{creator}
AND r.handle_dept= #{createDept}
AND r.state='2' AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time DESC

select t.id id, FUNC_GET_USERNAME(t.manager) manager, func_get_deptname(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd') requestDate, t.remark remark, t.reason reason, t.lowercase_amount amount, t.request_flag requestFlag, t."UPPERCASE AMOUNT" amountUpper, t.creator creator, t.create_time createTime from T_BG_Funds2_WF t
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND t.creator= #{creator}
AND t.create_dept= #{createDept}
AND t.request_flag = '0' ORDER BY t.create_time DESC
