mapper.select:
SELECT t.id id, FUNC_GET_USERNAME(t.requester) requester, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, t.goods_name goodsName, t.goods_num goodsNum, t.unit, t.reason reason, t.budget budget, t.remark remark, t.request_flag requestFlag, t.feedback feedback, t.feedback_flag feedbackFlag, t.creator creator, t.create_time createTime, t.create_dept createDept, t.valid_flag FROM T_ZW_GOODSPURCHASING_WF t WHERE 1 = 1
AND t.creator = #{creator}
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND t.create_dept = #{createDept}
AND t.request_flag = '0' ORDER BY t.create_time DESC

SELECT t.id , t.goods_name goodsName, t.goods_num goodsNum, t.unit, t.reason, t.budget, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd hh24:mi') as requestDate, FUNC_GET_USERNAME(t.requester) requester, t.remark, t.request_flag requestFlag, t.feedback, t.feedback_flag feedbackFlag, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_ZW_GOODSPURCHASING_WF t,t_sys_workflow_start s,t_sys_workflow_handle r WHERE 1 = 1
AND r.handle_user= #{creator}
AND func_get_deptname(t.request_dept) LIKE #{requestDept}
AND r.handle_dept= #{createDept}
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND FUNC_GET_USERNAME(t.requester) LIKE #{requester}
AND (r.state='1' or r.state='3')AND t.id = s.business_id AND s.start_id = r.start_id AND t.request_flag != '4' ORDER BY t.create_time DESC

SELECT DISTINCT t.id , t.goods_name goodsName, t.goods_num goodsNum, t.reason reason, t.budget budget, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_USERNAME(t.requester) requester, t.remark, FUNC_GET_DICVALUE(t.request_flag,'GZLLZZT') requestFlag, FUNC_GET_DICVALUE(t.feedback_flag,'GZLFKZT') feedbackFlag, t.feedback, t.creator, t.create_time, t.create_dept, t.valid_flag FROM T_ZW_GOODSPURCHASING_WF t,t_sys_workflow_start s,t_sys_workflow_handle r WHERE 1 = 1
AND r.handle_user= #{creator}
AND func_get_deptname(t.request_dept) LIKE #{requestDept}
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND FUNC_GET_USERNAME(t.requester) LIKE #{requester}
AND r.handle_dept LIKE #{createDept}
AND r.state='2' AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time DESC
