mapper.select:
SELECT t.id, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') studentId, FUNC_GET_TABLEVALUE(t.class_id,'T_XG_CLASS','CLASS_ID','CLASS_NAME') classId, FUNC_GET_TABLEVALUE(t.major_code,'t_xg_major','major_code','major_name') majorCode, FUNC_GET_TABLEVALUE(t.department_id,'T_SYS_DEPT','DEPT_ID','DEPT_NAME') departmentId, FUNC_GET_DICVALUE(t.sex,'XB') sexShow, FUNC_GET_DICVALUE(t.grant_management_term,'XQ') grantManagementTermShow, FUNC_GET_DICVALUE(t.GRANT_MANAGEMENT_NAME,'JZXJMC') grantManagementNameShow, t.request_flag, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_XG_GRANT_MANAGEMENT_WF t where 1 = 1 AND t.request_flag = '0'
AND t.creator= #{creator}
AND FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') LIKE '%'||#{studentId}||'%'
AND to_char(t.request_date,'yyyy-mm-dd') LIKE #{requestDate}
AND t.GRANT_MANAGEMENT_NAME = #{grantManagementName}
AND grant_management_term = #{grantManagementTerm}
AND t.sex = #{sex}
AND t.department_id= #{departmentId}
AND t.major_code= #{majorCode}
AND t.class_id= #{classId}
ORDER BY t.create_time desc

SELECT t.id, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') studentId, FUNC_GET_TABLEVALUE(t.class_id,'T_XG_CLASS','CLASS_ID','CLASS_NAME') classId, FUNC_GET_TABLEVALUE(t.major_code,'t_xg_major','major_code','major_name') majorCode, FUNC_GET_TABLEVALUE(t.department_id,'T_SYS_DEPT','DEPT_ID','DEPT_NAME') departmentId, FUNC_GET_DICVALUE(t.sex,'XB') sexShow, FUNC_GET_DICVALUE(t.grant_management_term,'XQ') grantManagementTermShow, FUNC_GET_DICVALUE(t.GRANT_MANAGEMENT_NAME,'JZXJMC') grantManagementNameShow, t.request_flag, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_XG_GRANT_MANAGEMENT_WF t,t_sys_workflow_start s,t_sys_workflow_handle r where 1 = 1
AND r.handle_user= #{creator}
AND FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') LIKE '%'||#{studentId}||'%'
AND to_char(t.request_date,'yyyy-mm-dd') LIKE #{requestDate}
AND t.GRANT_MANAGEMENT_NAME = #{grantManagementName}
AND grant_management_term = #{grantManagementTerm}
AND t.sex = #{sex}
AND t.department_id= #{departmentId}
AND t.major_code= #{majorCode}
AND t.class_id= #{classId}
AND (r.state='1' or r.state='3')AND t.id = s.business_id AND s.start_id = r.start_id AND t.request_flag != '4' ORDER BY t.create_time desc

SELECT DISTINCT t.id, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') studentId, FUNC_GET_TABLEVALUE(t.class_id,'T_XG_CLASS','CLASS_ID','CLASS_NAME') classId, FUNC_GET_TABLEVALUE(t.major_code,'t_xg_major','major_code','major_name') majorCode, FUNC_GET_TABLEVALUE(t.department_id,'T_SYS_DEPT','DEPT_ID','DEPT_NAME') departmentId, FUNC_GET_DICVALUE(t.sex,'XB') sexShow, FUNC_GET_DICVALUE(t.grant_management_term,'XQ') grantManagementTermShow, FUNC_GET_DICVALUE(t.GRANT_MANAGEMENT_NAME,'JZXJMC') grantManagementNameShow, FUNC_GET_DICVALUE(t.request_flag,'GZLLZZT') requestFlag, FUNC_GET_DICVALUE(t.feedback_flag,'GZLFKZT') feedbackFlag, t.feedback feedBack, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_XG_GRANT_MANAGEMENT_WF t,t_sys_workflow_start s,t_sys_workflow_handle r where 1 = 1
AND r.handle_user= #{creator}
AND FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') LIKE '%'||#{studentId}||'%'
AND to_char(t.request_date,'yyyy-mm-dd') LIKE #{requestDate}
AND t.GRANT_MANAGEMENT_NAME = #{grantManagementName}
AND grant_management_term = #{grantManagementTerm}
AND t.sex = #{sex}
AND t.department_id= #{departmentId}
AND t.major_code= #{majorCode}
AND t.class_id= #{classId}
AND r.state='2' AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time desc

SELECT DISTINCT t.id, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') studentId, FUNC_GET_TABLEVALUE(t.class_id,'T_XG_CLASS','CLASS_ID','CLASS_NAME') classId, FUNC_GET_TABLEVALUE(t.major_code,'t_xg_major','major_code','major_name') majorCode, FUNC_GET_DICVALUE(t.request_flag,'GZLLZZT') requestFlag, FUNC_GET_DICVALUE(t.feedback_flag,'GZLFKZT') feedbackFlag, t.feedback feedBack, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_RS_LEAVE_WF t,t_sys_workflow_start s,t_sys_workflow_handle r where 1 = 1 AND feedback_flag = 1
AND to_char(t.request_date,'yyyy-mm-dd') LIKE #{requestDate}
AND r.state='2' AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time desc

SELECT t.id, to_char(t.request_date,'yyyy-mm-dd hh24:mi') requestDate, FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') studentId, FUNC_GET_TABLEVALUE(t.class_id,'T_XG_CLASS','CLASS_ID','CLASS_NAME') classId, FUNC_GET_TABLEVALUE(t.major_code,'t_xg_major','major_code','major_name') majorCode, FUNC_GET_TABLEVALUE(t.department_id,'T_SYS_DEPT','DEPT_ID','DEPT_NAME') departmentId, FUNC_GET_DICVALUE(t.sex,'XB') sexShow, FUNC_GET_DICVALUE(t.grant_management_term,'XQ') grantManagementTermShow, FUNC_GET_DICVALUE(t.GRANT_MANAGEMENT_NAME,'JZXJMC') grantManagementNameShow, t.request_flag, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_XG_GRANT_MANAGEMENT_WF t where 1 = 1 AND t.request_flag = '2'
AND FUNC_GET_TABLEVALUE(t.student_id,'T_XG_STUDENT','STUDENT_ID','NAME') LIKE '%'||#{studentId}||'%'
AND t.GRANT_MANAGEMENT_NAME = #{grantManagementName}
AND grant_management_term = #{grantManagementTerm}
AND t.sex = #{sex}
AND t.department_id= #{departmentId}
AND t.major_code= #{majorCode}
AND t.class_id= #{classId}
ORDER BY t.create_time desc
