mapper.select:
       select id,type,func_get_dicvalue(type,'GLXXXTLX') typeShow,system_name,sources,func_get_dicvalue(sources,'GLXXXTLY') sourcesShow,unit_name,creator,create_time,create_dept,changer,change_time,change_dept,valid_flag from T_TAB_MANAGEMENT_INFORMATION where 1=1 
AND type = #{type}  
AND system_name like '%'||#{systemName}||'%'  
AND sources = #{sources}  
AND unit_name like '%'||#{unitName}||'%'
