mapper.select:
SELECT t.id id,
FUNC_GET_USERNAME(t.teacher_id) teacherId, FUNC_GET_DEPTNAME(t.dept_id) deptId, FUNC_GET_DICVALUE(t.meal_card_type, 'FKLX') mealCardTypeShow, t.meal_card_type mealCardType, to_char(t.meal_card_time,'yyyy-mm-dd')|| ' '|| to_char(t.meal_card_time,'hh24:mi') mealCardTime, /* to_char(t.meal_card_time,'yyyy-mm-dd hh24:mi') mealCardTime,*/ t.remark, t.request_flag, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_BG_MEALCARD_HANDLE_WF t where 1 = 1 AND t.request_flag = '0'
AND t.creator= #{creator}
AND to_char(t.meal_card_time,'yyyy-mm-dd') LIKE #{mealCardTime}
AND t.create_dept= #{createDept}
ORDER BY t.create_time desc

SELECT t.id id,
FUNC_GET_USERNAME(t.teacher_id) teacherId, FUNC_GET_DEPTNAME(t.dept_id) deptId, FUNC_GET_DICVALUE(t.meal_card_type, 'FKLX') mealCardTypeShow, t.meal_card_type mealCardType, to_char(t.meal_card_time,'yyyy-mm-dd')|| ' '|| to_char(t.meal_card_time,'hh24:mi') mealCardTime, /* to_char(t.meal_card_time,'yyyy-mm-dd hh24:mi') mealCardTime,*/ t.remark, t.request_flag, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_BG_MEALCARD_HANDLE_WF t,t_sys_workflow_start s,t_sys_workflow_handle r where 1 = 1
AND r.handle_user= #{creator}
AND func_get_deptname(t.dept_id) LIKE #{deptId}
AND r.handle_dept= #{createDept}
AND FUNC_GET_USERNAME(t.teacher_id) LIKE #{teacherId}
AND to_char(t.meal_card_time,'yyyy-mm-dd') LIKE #{mealCardTime}
AND (r.state='1' or r.state='3')AND t.id = s.business_id AND s.start_id = r.start_id AND t.request_flag != '4' ORDER BY t.create_time desc

SELECT DISTINCT t.id id,
FUNC_GET_USERNAME(t.teacher_id) teacherId, FUNC_GET_DEPTNAME(t.dept_id) deptId, FUNC_GET_DICVALUE(t.meal_card_type, 'FKLX') mealCardTypeShow, t.meal_card_type mealCardType, to_char(t.meal_card_time,'yyyy-mm-dd')|| ' '|| to_char(t.meal_card_time,'hh24:mi') mealCardTime, /*to_char(t.meal_card_time,'yyyy-mm-dd hh24:mi') mealCardTime,*/ t.remark, FUNC_GET_DICVALUE(t.request_flag,'GZLLZZT') requestFlag, FUNC_GET_DICVALUE(t.feedback_flag,'GZLFKZT') feedbackFlag, t.feedback feedBack, t.creator, t.create_time, t.create_dept, t.changer, t.change_time, t.change_dept, t.valid_flag FROM T_BG_MEALCARD_HANDLE_WF t,t_sys_workflow_start s,t_sys_workflow_handle r where 1 = 1
AND r.handle_user= #{creator}
AND func_get_deptname(t.dept_id) LIKE #{deptId}
AND FUNC_GET_USERNAME(t.teacher_id) LIKE #{teacherId}
AND r.handle_dept= #{createDept}
AND to_char(t.meal_card_time,'yyyy-mm-dd') LIKE #{mealCardTime}
AND r.state='2' AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time desc
