mapper.select:
select t.*, func_get_tablevalue(MT_ID, 'T_XG_MAINTENANCE_TYPE', 'MT_ID', 'MT_NAME') mtName, GOOD_NUM*UNIT_PRICE as goodPrice, FUNC_GET_DICVALUE(t.mt_flag,'ISMT') mtFalgShow from T_XG_MAINTENANCE_RELATION t where rel_id = #{relId} and valid_flag = '1'
and MT_ID = #{mtId}
and GOOD_NAME like '%' || #{goodName} || '%'

mapper.update:
update  T_XG_MAINTENANCE_RELATION t set MT_ID=#{mtId},GOOD_NAME=#{goodName}
,GOOD_UNIT=#{goodUnit} ,GOOD_NUM=#{goodNum} ,GOOD_CASE=#{goodCase}
,GOOD_REMARK=#{goodRemark},changer=#{changer},change_time=sysdate,change_dept=#{changeDept}
where t.MT_ID = #{mtId} and valid_flag = '1'

mapper.delete:
update T_XG_MAINTENANCE_RELATION t set t.valid_flag = '0',changer=#{changer},
change_time=sysdate,
change_dept=#{changeDept}
where id = #{id}

mapper.update:
update T_XG_MAINTENANCE_RELATION t set MT_FLAG='1',changer=#{changer},
change_time=sysdate,
change_dept=#{changeDept}
where id = #{id}
