mapper.select:
SELECT id, title, content, t.type, t.creator loginID, FUNC_GET_DICVALUE(type,'GGLX') typeShow, func_get_username(creator) creator, func_get_deptname(create_dept) createDept, to_char(t.create_time,'yyyy-mm-dd') publicTime, FUNC_GET_DICVALUE(publish_flag,'TGFBZT') publishFlag, t.request_flag requestFlag, dept_id deptId FROM T_SYS_NOTICE t WHERE 1 = 1 AND valid_flag ='1' AND request_flag='2' AND t.title is not null
AND title LIKE #{title}
AND type LIKE #{type}
ORDER BY create_time DESC

SELECT id, title, content, t.type, t.creator loginID, FUNC_GET_DICVALUE(type,'GGLX') typeShow, func_get_username(creator) creator, func_get_deptname(create_dept) createDept, to_char(t.create_time,'yyyy-mm-dd') publicTime, FUNC_GET_DICVALUE(publish_flag,'TGFBZT') publishFlag, dept_id deptId FROM T_SYS_NOTICE t WHERE 1 = 1 AND valid_flag ='1' AND publish_flag='1' AND t.title is not null
AND title LIKE #{title}
AND type LIKE #{type}
ORDER BY create_time DESC

SELECT id, title,content, type, public_time publicTime , FUNC_GET_DICVALUE(t.type,'GGLX') typeShow,create_time createTime,dept_id deptId FROM T_SYS_NOTICE t WHERE 1 = 1 AND valid_flag ='1' and request_flag='2' and DEPT_ID LIKE FUNC_GET_RANGE(#{defaultDeptId},#{level})
AND t.title LIKE #{title}
AND t.type LIKE #{type}
ORDER BY create_time DESC

SELECT t.id, t.title, t.content, t.type, FUNC_GET_DICVALUE(t.type,'GGLX') typeShow, func_get_username(t.creator) creator, func_get_deptname(t.create_dept) createDept, to_char(t.create_time,'yyyy-mm-dd') publicTime, FUNC_GET_DICVALUE(t.publish_flag,'TGFBZT') publishFlag, t.dept_id deptId FROM T_SYS_NOTICE t,t_sys_workflow_start s,t_sys_workflow_handle r WHERE 1 = 1 AND t.valid_flag ='1'
AND t.title LIKE '%' || #{title} || '%'
AND t.type LIKE '%'|| #{type} || '%'
AND r.handle_user= #{requester}
AND r.handle_dept= #{requestDept}
AND (r.state='1' or r.state = '3') AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time DESC

SELECT id, title, content, t.type, FUNC_GET_DICVALUE(type,'GGLX') typeShow, func_get_username(creator) creator, func_get_deptname(create_dept) createDept, to_char(t.create_time,'yyyy-mm-dd') publicTime, FUNC_GET_DICVALUE(publish_flag,'TGFBZT') publishFlag, dept_id deptId FROM T_SYS_NOTICE t WHERE 1 = 1 AND valid_flag ='1' and request_flag='0' and publish_flag='2' and workflow_flag='1'
AND t.creator= #{requester}
AND t.create_dept= #{requestDept}
AND title LIKE #{title}
AND type LIKE #{type}
ORDER BY create_time DESC

mapper.update:
UPDATE T_SYS_NOTICE SET
publish_flag='2'
WHERE id = #{id}

mapper.select:
SELECT DISTINCT t.id, t.title, to_char(t.content)content, t.type, FUNC_GET_DICVALUE(t.type,'GGLX') typeShow, func_get_username(t.creator) creator, func_get_deptname(t.create_dept) createDept, to_char(t.create_time,'yyyy-mm-dd') publicTime, FUNC_GET_DICVALUE(t.publish_flag,'TGFBZT') publishFlag, t.dept_id deptId, t.create_time FROM T_SYS_NOTICE t,T_SYS_WORKFLOW_START s,T_SYS_WORKFLOW_HANDLE r WHERE 1 = 1
AND t.title LIKE '%' || #{title} || '%'
AND t.type LIKE '%' || #{type} || '%'
AND FUNC_GET_USERNAME(t.creator) LIKE #{creator}
AND func_get_deptname(t.create_dept) LIKE #{createDept}
AND r.handle_user= #{requester}
AND r.handle_dept= #{requestDept}
AND r.state='2' AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time DESC

mapper.update:
UPDATE T_SYS_NOTICE SET
publish_flag='1'
WHERE id = #{id}
