mapper.select:
select t.id id, FUNC_GET_USERNAME(t.requester) requester, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd') as requestDate, t.remark remark, func_get_userdicvalue(t.item_name,'BGHC') itemNameShow, t.item_name itemName, t.item_number itemNumber, t.creator creator, t.create_time createTime, t.create_dept createDept, t.changer changer, t.change_time changeTime, t.change_dept changeDept, t.valid_flag validFlag from T_BG_OFFICEITEM_WF t WHERE 1 = 1
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND t.creator= #{creator}
AND t.create_dept= #{createDept}
AND t.request_flag = '0' ORDER BY t.create_time DESC

select t.id id, FUNC_GET_USERNAME(t.requester) requester, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd ') requestDate, t.remark remark, t.item_name itemName, func_get_userdicvalue(t.item_name,'BGHC') itemNameShow, t.item_number itemNumber
from T_BG_OFFICEITEM_WF t,T_SYS_WORKFLOW_START s,T_SYS_WORKFLOW_HANDLE r WHERE 1 = 1
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND FUNC_GET_USERNAME(t.requester) LIKE #{requester}
AND FUNC_GET_DEPTNAME(t.request_dept) LIKE #{requestDept}
AND r.handle_user= #{creator}
AND r.handle_dept= #{createDept}
AND (r.state='1' or r.state = '3') AND t.id = s.business_id AND s.start_id = r.start_id AND t.request_flag != '4' ORDER BY r.create_time DESC

SELECT DISTINCT t.id, FUNC_GET_USERNAME(t.requester) requester, FUNC_GET_DEPTNAME(t.request_dept) requestDept, to_char(t.request_date,'yyyy-mm-dd ') as requestDate, t.remark remark, func_get_userdicvalue(t.item_name,'BGHC') itemName, func_get_userdicvalue(t.item_name,'BGHC') itemNameShow, t.item_number itemNumber, t.feedback feedback, FUNC_GET_DICVALUE(t.request_flag,'GZLLZZT') requestFlag, FUNC_GET_DICVALUE(t.feedback_flag,'GZLFKZT') feedbackFlag, t.creator creator, t.create_time createTime, t.create_dept createDept, t.changer changer, t.change_time changeTime, t.change_dept changeDept, t.valid_flag validFlag from T_BG_OFFICEITEM_WF t,T_SYS_WORKFLOW_START s,T_SYS_WORKFLOW_HANDLE r WHERE 1 = 1
AND to_char(t.request_date, 'yyyy-mm-dd') LIKE #{requestDate}
AND FUNC_GET_USERNAME(t.requester) LIKE #{requester}
AND FUNC_GET_DEPTNAME(t.request_dept) LIKE #{requestDept}
AND r.handle_user= #{creator}
AND r.handle_dept= #{createDept}
AND r.state='2' AND t.id = s.business_id AND s.start_id = r.start_id ORDER BY t.create_time DESC
