mapper.select:
select t1.id, t1.personid, t1.grade, func_get_dicvalue(t1.grade,'ZYDJ') gradeShow, t1.givenname, t1.issuer, t1.getdate, t1.teachingmanagement, t1.studentmanagement, t1.politicalcounselor, t1.psychologicalconsultant, t1.employmentoffice, t1.expertise, t1.workinghours, t2.name from T_TAB_STAFF t1 ,T_RS_EMPLOYEE t2 where 1=1 and t1.personid = t2.person_id and t1.type = #{type}
and t1.GIVENNAME like #{groupNameSel}
AND t1.personid = #{personid}

mapper.update:
update T_TAB_STAFF set

    PERSONID = #{personidvalue} ,


grade=#{grade},givenname=#{givenname},issuer=#{issuer},getdate=#{getdate}


    ,teachingmanagement=#{teachingmanagement}


    ,studentmanagement=#{studentmanagement},politicalcounselor=#{politicalcounselor},psychologicalconsultant=#{psychologicalconsultant}


    ,employmentoffice=#{employmentoffice}



    ,expertise=#{expertise},workinghours=#{workinghours}


where id = #{id}
