mapper.select:
SELECT t.TEACHER_ID teacherId, FUNC_GET_USERNAME(t.NAME) teacherName, to_char(t.BIRTHDAY,'yyyy-mm-dd') birthday, FUNC_GET_DICVALUE(t.FINAL_EDUCATION, 'XL') finalEducation, FUNC_GET_DICVALUE(t.DEGEE,'XW') DEGEE, t.MAJOR majorShow, t.TITLE title, FUNC_GET_DICVALUE(t.TITLE, 'ZCJB') titleShow, t.TEACHER_CATEGORY teacherCategory, FUNC_GET_DICVALUE(t.TEACHER_CATEGORY, 'JSLB') teacherCategoryShow, t.CREATOR creator, t.CREATE_TIME createTime FROM T_JW_TEACHER_CONDITION t WHERE 1 = 1
AND t.TEACHER_ID = #{teacherId}
AND t.DEPARTMENT_ID LIKE #{departmentId}
AND t.MAJOR= #{major}
AND FUNC_GET_USERNAME(t.NAME) LIKE '%'||#{teacherName}||'%'
ORDER BY t.CREATE_TIME DESC

SELECT t.TEACHER_ID teacherId, t.TEACHER_NAME personId, FUNC_GET_USERNAME(t.TEACHER_NAME) teacherName, DEPARTMENT_ID departmentId, FUNC_GET_DEPTNAME(t.DEPARTMENT_ID) departmentIdShow, t.TEACHING_LEVEL teachingLevel, FUNC_GET_DICVALUE(t.TEACHING_LEVEL, 'JSJLJB') teachingLevelShow, t.SUBJECT subject, FUNC_GET_TABLEVALUE(t.SUBJECT, 'T_JW_COURSE', 'COURSE_ID', 'COURSE_NAME') subjectShow, to_char(t.ISSUING_TIME,'yyyy-mm-dd') issuingTime, t.ISSUING_DEPT issuingDept, t.VALIDITY_TERM validityTerm, t.CREATOR creator, t.CREATE_TIME createTime FROM T_JW_DOUBLE_DIVISION_TEACHER t WHERE 1 = 1
AND FUNC_GET_USERNAME(t.TEACHER_NAME) LIKE '%${teacherName}%'
AND t.TEACHING_LEVEL= #{teachingLevel}
AND t.SUBJECT= #{subject}
ORDER BY t.CREATE_TIME DESC

SELECT t.TEACHER_ID teacherId, t.TEACHER_NAME personId, FUNC_GET_USERNAME(t.TEACHER_NAME) teacherName, t.HONOR_NAME honorName, t.GET_TIME getTime, t.HONOR_LEVEL honorLevel, FUNC_GET_DICVALUE(t.HONOR_LEVEL, 'RYJB') honorLevelShow, t.CREATOR creator, t.CREATE_TIME createTime FROM T_JW_GETHONOR t WHERE 1 = 1
AND FUNC_GET_USERNAME(t.TEACHER_NAME) LIKE '%${teacherName}%'
AND t.HONOR_NAME= #{honorName}
AND t.GET_TIME= #{getTime}
ORDER BY t.CREATE_TIME DESC

SELECT t.TEACHER_ID teacherId, t.TEACHER_NAME personId, FUNC_GET_USERNAME(t.TEACHER_NAME) teacherName, FUNC_GET_DICVALUE(t.EDUCATION, 'XL') education, FUNC_GET_DICVALUE(t.DEGEE, 'XW') degee, t.FINISH_SCHOOL finishSchool, t.MAJOR major, t.MAJOR majorShow, t.GET_TIME getTime, t.CREATOR creator, t.CREATE_TIME createTime FROM T_JW_EDUCATION_IMPROVE t WHERE 1 = 1
AND FUNC_GET_USERNAME(t.TEACHER_NAME) LIKE '%${teacherName}%'
AND t.EDUCATION= #{education}
AND t.degee= #{degee}
ORDER BY t.CREATE_TIME DESC
