mapper.select:
select id, (SELECT DEPT_NAME FROM T_SYS_DEPT WHERE DEPT_ID = department_id) departmentId, (select MAJOR_NAME from T_XG_MAJOR where T_XG_MAJOR.MAJOR_CODE = T_JW_TIMETABLE_DEPARTMENT.MAJOR_ID) majorId, (select CLASS_NAME from T_XG_CLASS where T_XG_CLASS.CLASS_ID = T_JW_TIMETABLE_DEPARTMENT.CLASS_ID) classId, people_number, (select class_room_name from T_JW_CLASSROOM where id = class_rom) classRom from T_JW_TIMETABLE_DEPARTMENT
where TIME_TABLE_ID= #{timeTableId}
and DEPARTMENT_ID=#{departmentId}

select MAJOR_CODE id,MAJOR_NAME text
from T_XG_MAJOR
where DEPARTMENTS_ID = #{id}

select CLASS_ID id,CLASS_NAME text
from T_XG_CLASS
where MAJOR_CODE = #{id}
