mapper.select:
SELECT DISTINCT r.resource_id resourceid, r.resource_name resourcename, r.parent_resource_id parentresourceid, r.resource_url resourceurl, r.resource_type resourcetype, r.resource_order resourceorder FROM t_sys_role_resource rr, t_sys_resource r WHERE rr.role_id in (${roles}) AND r.resource_id = rr.resource_id AND r.resource_type = '1' AND SYSTEM = #{system}
AND r.parent_resource_id = #{parentId}
ORDER BY r.resource_order
